dbModule.controller("BookmarkController", ['$scope', 'Bookmarks', 'DocumentApi', 'Database', 'scroller', 'Aside', function ($scope, Bookmarks, DocumentApi, Database, scroller, Aside) {


  $scope.closeIfReturn = function (e) {
    if (e.keyCode == '27') {
      $scope.click();
    }
  }
  $scope.$on("bookmarks:changed", function (event) {
    Bookmarks.getAll(Database.getName()).then(function (data) {
      $scope.bks = data.result;

    });
  });

  Bookmarks.getAll(Database.getName()).then(function (data) {
    $scope.bks = data.result
  });

  $scope.isSelected = function (bk) {
    return $scope.selected == bk ? '' : 'hide';
  }
  $scope.hover = function (bk) {
    $scope.selected = bk;
  }
  $scope.run = function (r) {
    $scope.queryText = r.query;
    if (r.selectedRequestType) {
      $scope.config.selectedRequestType = r.selectedRequestType;
    }
    if (r.selectedRequestLanguage) {
      $scope.config.selectedRequestLanguage = r.selectedRequestLanguage;
    }
    scroller.scrollTo(0, 0, 2000);
    $scope.cm.focus();

    $scope.cm.setValue($scope.queryText);
    $scope.cm.setCursor($scope.cm.lineCount());
    Aside.toggle();
  }

  $scope.stopProps = function ($event) {
    if ($event) {
      $event.stopPropagation();
    }
  }
  $scope.remove = function (r, $event) {
    if ($event) {
      $event.stopPropagation();
    }
    Bookmarks.remove(Database.getName(), r).then(function (data) {
      var idx = $scope.bks.indexOf(r);
      $scope.bks.splice(idx, 1);
    });

  }
  $scope.update = function (r) {
    Bookmarks.update(Database.getName(), r).then(function (data) {
      var idx = $scope.bks.indexOf(r);
      $scope.bks.splice(idx, 1, data);
    });
  }
}]);

dbModule.controller("ProfilerController", ['$scope', 'Profiler', 'Database', 'Spinner', 'Notification', function ($scope, Profiler, Database, Spinner, Notification) {


  var metricName = 'db.' + Database.getName() + '.command.';

  $scope.itemsByPage = 4;
  $scope.profiles = []


  $scope.refresh = function () {
    Spinner.start();
    Profiler.profilerData(Database.getName()).then(function (data) {
      var profiling = $scope.flatten(data.realtime.chronos, metricName);
      $scope.profiles = profiling;
      $scope.safeCopy = angular.copy(profiling);
      Spinner.stopSpinner();
    }).catch(function (error) {
      if (error.status == 405) {
        Notification.push({content: error.data, error: true, autoHide: true});
      } else {
        Notification.push({content: error.data, error: true, autoHide: true});
      }

      Spinner.stopSpinner();
    })
  }


  $scope.flatten = function (result, metricName) {
    var commands = new Array;

    Object.keys(result).forEach(function (e, i, a) {
      var obj = {};
      obj.name = e.substring(metricName.length, e.length);
      Object.keys(result[e]).forEach(function (ele, ide, arr) {
        obj[ele] = result[e][ele];
      });

      commands.push(obj);

    });
    return commands;
  }
  $scope.$watch('profiles', function (data) {

  })
  $scope.refresh();
}]);


dbModule.controller("AuditingController", ['$scope', 'Auditing', 'Database', 'Spinner', 'Notification', '$modal', 'ngTableParams', function ($scope, Auditing, Database, Spinner, Notification, $modal, ngTableParams) {


  $scope.active = 'log';

  $scope.query = {
    limit: 100
  }

  $scope.template = 'views/database/auditing/log.html';
  Auditing.getConfig(Database.getName()).then(function (data) {
    $scope.config = data;
    var cls = $scope.config.classes;
    $scope.classes = Object.keys(cls).filter(function (k) {
      return (k != "@type" && k != "@version")
    }).map(function (k) {

      var clazz = {
        name: k,
        polymorphic: cls[k].polymorphic
      }

      return clazz;

    })

    $scope.query.clazz = $scope.config.auditClassName;

    Spinner.start();
    Auditing.query(Database.getName(), $scope.query).then(function (data) {
      $scope.logs = data.result;

      $scope.tableParams = new ngTableParams({
        page: 1,            // show first page
        count: 10          // count per page

      }, {
        total: $scope.logs.length, // length of data
        getData: function ($defer, params) {
//            use build-in angular filter
          var emtpy = !params.orderBy() || params.orderBy().length == 0;
          var orderedData = (params.sorting() && !emtpy) ?
            $filter('orderBy')($scope.logs, params.orderBy()) :
            $scope.logs;
          $defer.resolve(orderedData.slice((params.page() - 1) * params.count(), params.page() * params.count()));
        }
      });
      Spinner.stopSpinner();
    }).catch(function (error) {
      Spinner.stopSpinner();
    })
  })

  $scope.resetFilter = function () {
    $scope.query = {
      limit: 100,
      clazz: $scope.config.auditClassName
    }
  }
  $scope.save = function () {

    Auditing.saveConfig(Database.getName(), $scope.config).then(function () {
      Notification.push({content: "Auditing configuration saved.", autoHide: true});
    }).catch(function (error) {

    })
  }

  $scope.filter = function () {
    Spinner.start();
    Auditing.query(Database.getName(), $scope.query).then(function (data) {
      $scope.logs = data.result;


      $scope.tableParams.total($scope.logs.length);
      $scope.tableParams.reload();
      Spinner.stopSpinner();
    }).catch(function (error) {
      Spinner.stopSpinner();
    })
  }
  $scope.$watch("active", function (val) {
    switch (val) {
      case "config":
        $scope.template = 'views/database/auditing/config.html';
        break;
      case "log":
        $scope.template = 'views/database/auditing/log.html';
        break;
    }
  })
  $scope.delete = function (k) {
    delete $scope.config.classes[k];
  }
  $scope.addCommand = function () {
    if (!$scope.config.commands) {
      $scope.config.commands = new Array;
    }
    $scope.config.commands.push({
      regex: "",
      message: ""
    })
  }
  $scope.deleteCommand = function (index) {
    $scope.config.commands.splice(index, 1);
  }
  $scope.addClass = function () {
    var modalScope = $scope.$new(true);
    modalScope.classes = Database.listClasses();
    var modalPromise = $modal({template: 'views/database/auditing/newClass.html', scope: modalScope, show: false});

    modalScope.save = function () {
      if (modalPromise.$scope.selectedClass) {
        var cfg = {
          "polymorphic": true,
          "onCreateEnabled": false,
          "onCreateMessage": "",
          "onReadEnabled": false,
          "onReadMessage": "",
          "onUpdateEnabled": false,
          "onUpdateMessage": "",
          "onDeleteEnabled": false,
          "onDeleteMessage": ""
        }
        $scope.config.classes[modalPromise.$scope.selectedClass.name] = cfg;

      }
    }
    modalPromise.$promise.then(modalPromise.show);
  }
}])
;


