database.factory('Auditing', function ($http, $resource, $q, CommandApi) {


  var resource = $resource('');


  resource.getConfig = function (database) {

    var deferred = $q.defer();
    var text = API + 'auditing/' + database;
    $http.get(text).success(function (data) {
      deferred.resolve(data)
    }).error(function (data, status, headers, config) {
      deferred.reject({data: data, status: status});
    });
    return deferred.promise;
  }
  resource.saveConfig = function (database, cfg) {
    var deferred = $q.defer();
    var text = API + 'auditing/' + database;
    $http.post(text, cfg).success(function (data) {
      deferred.resolve(data)
    }).error(function (data, status, headers, config) {
      deferred.reject({data: data, status: status});
    });
    return deferred.promise;
  }
  resource.query = function (database, args) {

    var deferred = $q.defer();
    var query = "select from {{clazz}} {{where}} order by date desc limit {{limit}} fetchPlan user:1";
    args.where = "";

    if (args.user) {
      args.where += " user.name = '{{user}}' and"
    }
    if (args.record) {
      args.where += " record  = '{{record}}' and"
    }
    if (args.operation) {
      args.where += " operation  = '{{operation}}' and"
    }
    if (args.from) {
      args.where += " date  > '{{from}}' and"
    }
    if (args.to) {
      args.where += " date  < '{{to}}' and"
    }
    if (args.note) {
      args.where += " note like '%{{note}}%' and"
    }
    if (args.where != "") {
      var n = args.where.lastIndexOf("and");
      args.where = " where " + args.where.substring(0, n);
    }
    var queryText = S(query).template(args).s;
    if (args.where != "") {
      queryText = S(queryText).template(args).s
    }
    var params = {
      database: database,
      text: queryText,
      language: 'sql',
      verbose: false
    }
    CommandApi.queryText(params, function (data) {
      deferred.resolve(data)
    }, function (data) {
      deferred.reject(data);
    })


    return deferred.promise;
  }
  return resource
});

var notification = angular.module('notification.services', []);

notification.factory('Notification', function ($timeout, $rootScope) {


  var notiService = {
    notifications: new Array,
    errors: new Array,
    warnings: new Array,

    push: function (notification) {


      this.notifications.splice(0, this.notifications.length);
      this.errors.splice(0, this.errors.length);
      this.warnings.splice(0, this.warnings.length);


      if (notification.error) {
        if (typeof notification.content != 'string') {
          notification.content = notification.content.errors[0].content;
        }
        this.errors.push(notification);
      } else if (notification.warning) {
        this.warnings.push(notification);
      }
      else {
        this.notifications.push(notification);
      }
      var self = this;
      if (!notification.error) {
        self.stopTimer();
        self.startTimer();
      }
    },
    startTimer: function () {
      var self = this;
      self.timePromise = $timeout(function () {
        self.clear();
      }, 4000)
    },
    stopTimer: function () {
      var self = this;
      if (self.timePromise) {
        $timeout.cancel(self.timePromise);
      }
    },
    clear: function () {
      this.notifications.splice(0, this.notifications.length);
      this.errors.splice(0, this.errors.length);
      this.warnings.splice(0, this.warnings.length);
    }

  }

  $rootScope.$on('alert:hover', function () {
    notiService.stopTimer();
  })
  $rootScope.$on('alert:out', function () {
    notiService.startTimer();
  })
  return notiService;
});

