graph.directive('c3chart', function ($http, $compile, $timeout, $rootScope) {

  var linker = function (scope, element, attrs) {


    var lastC = null;
    var lastR = null;
    var lastU = null;
    var lastD = null;
    element.id = scope.server.name;

    var counter = 0;
    $timeout(function () {
      scope.chart = c3.generate({
        bindto: "#" + element.id,
        data: {
          x: 'x',
          columns: [
            ['x', new Date()],
            ['Create', 0],
            ['Read', 0],
            ['Update', 0],
            ['Delete', 0],
          ]
        },
        point: {
          show: false
        },
        axis: {
          x: {
            type: 'timeseries',
            tick: {
              culling: {
                max: 4 // the number of tick texts will be adjusted to less than this value
              },
              format: '%H:%M:%S',
            }
          },
          y: {
            label: {
              text: 'Server ' + scope.server.name + ' CRUD'
            }
          }
        }
      });

      var countOps = function (array, regexp) {

        var keys = Object.keys(array).filter(function (k) {
          return k.match(regexp) != null;
        })

        var ops = 0;
        keys.forEach(function (k) {
          ops += array[k].entries;
        });
        return ops;
      }
      $rootScope.$on('server:updated', function (evt, server) {
        if (!server.name || server.name == scope.server.name) {

          var cOps = countOps(server.realtime['chronos'], /db.*createRecord/g);

          var rOps = countOps(server.realtime['chronos'], /db.*readRecord/g);

          var uOps = countOps(server.realtime['chronos'], /db.*updateRecord/g);

          var dOps = countOps(server.realtime['chronos'], /db.*deleteRecord/g);


          var createOperation = 0;
          var readOperation = 0;
          var updateOperation = 0;
          var deleteOperation = 0;

          if (lastC != null) {
            createOperation = Math.abs(lastC - cOps);
          }
          lastC = cOps;
          if (lastR != null) {
            readOperation = Math.abs(lastR - rOps);
          }
          lastR = rOps;
          if (lastU != null) {
            updateOperation = Math.abs(lastU - uOps);
          }
          lastU = uOps;
          if (lastD != null) {
            deleteOperation = Math.abs(lastD - dOps);
          }
          lastD = dOps;

          scope.chart.flow({
            columns: [
              ['x', new Date()],
              ['Create', createOperation],
              ['Read', readOperation],
              ['Update', updateOperation],
              ['Delete', deleteOperation],
            ],
            length: 0,
            duration: 1500
          })
          counter++;
          if (counter == 60) {
            counter = 0;
            lastC = null;
            lastR = null;
            lastU = null;
            lastD = null;
            scope.chart.load({
              columns: [
                ['x', new Date()],
                ['Create', 0],
                ['Read', 0],
                ['Update', 0],
                ['Delete', 0],
              ],
              unload: ['x', "Create", "Read", "Update", "Delete"]

            })
          }
        }
      })
    });


  }
  return {
    restrict: 'A',
    link: linker
  }
});

